import styles from "./ProfileDropdown.module.css";
import React, { useState, useEffect, useRef } from "react";
import profile from "./profile.png";
import { Link, useLocation } from "react-router-dom";

const ProfileDropdown = () => {
  const { pathname } = useLocation();
  const [open, setOpen] = useState(true);
  const [close, setClose] = useState(true);
  const [currentPath, setCurrentPath] = useState(pathname);

  // useEffect(() => {
  //   setCurrentPath(pathname);
  // }, []);

  useEffect(() => {
    if (open) {
      setOpen(false);
      setClose(true);
    }
  }, [pathname]);

  useEffect(() => {
    document.addEventListener("click", handleClickOutside, false);
    return () => {
      document.removeEventListener("click", handleClickOutside, false);
    };
  }, []);

  const wrapperRef = useRef(null);
  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setOpen(false);
    }
  };

  return (
    <div className={styles.container}>
      <li className={`${styles.option} `}>
        <div
          className={styles.profilePicContainer}
          onClick={() =>
            !open
              ? [setOpen(true), setClose(false)]
              : [setOpen(false), setClose(true)]
          }
        >
          <img src={profile} alt="" className={styles.profilePic} />
        </div>
        <div className={`${styles.profileDropdownBackground}`}>
          <div
            className={`${styles.profileDropdownContainer} ${
              open
                ? // && currentPath === pathname
                  styles.active
                : close
                ? styles.deactivated
                : null
            }  `}
            ref={wrapperRef}
          >
            <ul
              className={`${styles.profileDropdownList} ${
                open ? styles.active : null
              }`}
            >
              <li>
                <Link
                  to="/profile"
                  className={`${styles.option} ${open ? styles.active : null}`}
                >
                  Profile
                </Link>
              </li>
              <li>
                <Link
                  to="/"
                  className={`${styles.option} ${open ? styles.active : null}`}
                >
                  Log Out
                </Link>
              </li>
            </ul>
          </div>
        </div>

        {/* <Link to="/landing" className={styles.option}>

      </Link> */}
      </li>
    </div>
  );
};

export default ProfileDropdown;
