import styles from "./NavBar.module.css";
import React, { useState, useEffect } from "react";
import SideNav from "../SideNav/SideNav";
import { useQuery, useMutation } from "@apollo/client";
import { setCurrentUser } from "../../../redux/features/currentUserSlice";
import currentUserQuery from "../../../GQL/queries/CurrentUser";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import ProfileDropdown from "../ProfileDropdown/ProfileDropdown";

const NavBar = () => {
  let { loading, error, data } = useQuery(currentUserQuery);
  const [isUser, setIsUser] = useState();
  const currentUser = useAppSelector((state) => [
    state.currentUser,
    console.log(state),
  ]);
  const dispatch = useAppDispatch(false);

  /* -------------------------------------------------------------------------- */
  useEffect(() => {
    const fetchAccounts = () => {
      if (data) {
        dispatch(setCurrentUser(data.user2));
        setIsUser(true);
        // console.log(currentUser);
      } else {
        console.log("bro");
        setIsUser(false); //?????
      }
    };
    fetchAccounts();
  }, [loading, currentUser, data, dispatch]);

  /* -------------------------------------------------------------------------- */
  useEffect(() => {
    // console.log(currentUser);
  }, [setIsUser]);
  return (
    <div className={styles.container}>
      <SideNav />
      <ProfileDropdown />
    </div>
  );
};

export default NavBar;
