import styles from "./TestDropDown.module.css";
import React, {
  useState,
  useEffect,
  useRef,
  useContext,
  FC,
  SetStateAction,
  Dispatch,
} from "react";
import { Link, useLocation } from "react-router-dom";
import { useQuery, useMutation } from "@apollo/client";
import query from "../../../GQL/queries/CurrentUser";
import mutation from "../../../GQL/mutations/Logout";
import profile from "./profile.png";

const TestDropDown = () => {
  let [logout] = useMutation(mutation);
  let { loading, error, data } = useQuery(query);
  const { pathname } = useLocation();
  const [open, setOpen] = useState(true);
  const [close, setClose] = useState(false);
  const [currentPath, setCurrentPath] = useState(pathname);

  // useEffect(() => {
  //   if (open) {
  //   }
  // }, [pathname]);

  useEffect(() => {
    document.addEventListener("click", handleClickOutside, false);
    return () => {
      document.removeEventListener("click", handleClickOutside, false);
    };
  }, []);

  const wrapperRef = useRef(null);

  const onLogoutCLick = () => {
    logout({
      refetchQueries: [{ query: query }],
      awaitRefetchQueries: true,
    }).catch((err) => console.log(err));
  };
  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setOpen(false);
      // setClose(true);
    }
  };

  return (
    <div
      className={styles.container}
      ref={wrapperRef}
      onMouseLeave={() => [setOpen(false), setClose(true)]}
    >
      <div className={styles.profilePicContainer}>
        <img
          src={profile}
          alt=""
          className={styles.profilePic}
          onMouseOver={() => setOpen(true)}
          // onMouseLeave={() => setOpen(false)}
        />
      </div>
      {/* -------------------------------------------------------------------------- */}
      <div className={`${styles.profileDropdownContainer}`}>
        <div
          // onMouseLeave={() => setOpen(false)}
          className={`${styles.profileDropdownContainer} ${
            open
              ? // && currentPath === pathname
                styles.active
              : close
              ? styles.deactivated
              : // && styles.active
                null
          }  `}
        >
          <ul
            className={`${styles.profileDropdownList} ${
              open ? styles.active : null
            }`}
          >
            <li onClick={() => [setOpen(false), setClose(true)]}>
              <Link
                to="/profile"
                className={`${styles.option} ${open ? styles.active : null}`}
              >
                Profile
              </Link>
            </li>
            <li
              onClick={() => [onLogoutCLick(), setOpen(false), setClose(true)]}
            >
              <Link
                to="/login"
                className={`${styles.option} ${open ? styles.active : null}`}
              >
                Log Out
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TestDropDown;
