import styles from "./NavBar.module.css";
import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import SideNav from "../SideNav/SideNav";
import logo from "./logo.png";
import profile from "./profile.png";
import { useQuery, useMutation } from "@apollo/client";
import { setCurrentUser } from "../../../redux/features/currentUserSlice";
import query from "../../../GQL/queries/CurrentUser";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import ProfileDropdown from "../ProfileDropdown/ProfileDropdown";

const NavBar = () => {
  let { loading, error, data } = useQuery(query);
  const { pathname } = useLocation();
  const routesToNOTShowUpIn = ["/"];
  const shouldShowUp = !routesToNOTShowUpIn.includes(pathname);
  const [open, setOpen] = useState(false);
  const [activeBtn, setActiveBtn] = useState(0);
  const [user, setUser] = useState("");
  const [isUser, setIsUser] = useState();
  const currentUser = useAppSelector((state) => state.currentUser.value);
  const dispatch = useAppDispatch(false);

  useEffect(() => {
    const fetchAccounts = async () => {
      if (data) {
        dispatch(setCurrentUser(data.user2));
        setIsUser(true);
      } else {
        setIsUser(false);
      }
    };
    fetchAccounts();
  }, [loading, currentUser, data]);

  const signinContent = (
    <li
      onClick={() => setActiveBtn(1)}
      className={`${styles.option} ${activeBtn === 1 ? styles.active : null}`}
    >
      <Link to="/login" className={styles.option}>
        Log In
      </Link>
    </li>
  );
  const registerContent = (
    <li
      onClick={() => setActiveBtn(2)}
      className={`${styles.option} ${activeBtn === 2 ? styles.active : null}`}
    >
      <Link to="/register" className={styles.option}>
        Register
      </Link>
    </li>
  );
  /* -------------------------------------------------------------------------- */

  return (
    <div className={styles.container}>
      <SideNav />
      <ProfileDropdown />
    </div>
  );
};

export default NavBar;
