import styles from "./SideBar.module.css";
import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const SideBar = () => {
  const [open, setOpen] = useState(false);
  const { pathname } = useLocation();

  const routesToShowUpIn = ["/home", "/chat", "/friends"];
  const hi = routesToShowUpIn.includes(pathname);
  console.log(pathname);
  console.log(hi);

  if (hi) {
    return (
      <div className={styles.container}>
        <ul className={styles.optionList}>
          <li>
            <Link to="/home" className={styles.option}>
              <img
                // src="http://assets.stickpng.com/thumbs/588a668cd06f6719692a2d1b.png"
                alt=""
              />
              Home
            </Link>
          </li>
          <li>
            <Link to="/chat" className={styles.option}>
              <img
                src="https://www.pngkit.com/png/detail/252-2529173_sms-chat-message-information-memo-whatsapp-comments-whatsapp.png"
                alt=""
              />
              Chat
            </Link>
          </li>
          <li>
            <Link to="/friends" className={styles.option}>
              <img
                // src="https://icon-library.com/images/friends-icon-transparent/friends-icon-transparent-1.jpg"
                alt=""
              />
              Friends
            </Link>
          </li>
        </ul>
      </div>
    );
  } else {
    return null;
  }
};

export default SideBar;
