import styles from "./NavBar.module.css";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const NavBar = () => {
  const [open, setOpen] = useState(false);
  const [activeBtn, setActiveBtn] = useState(1);

  const renderLogoutOrSigninRegister = () => {};
  return (
    <div className={styles.container}>
      <div className={styles.hamburger} onClick={() => setOpen(!open)}>
        <span className={styles.line}></span>
        <span className={styles.line}></span>
        <span className={styles.line}></span>
      </div>

      <ul className={`${styles.optionList} ${open ? styles.open : null}`}>
        <li
          onClick={() => setActiveBtn(1)}
          className={`${styles.option} ${
            activeBtn === 1 ? styles.active : null
          }`}
        >
          <Link to="/home" className={styles.option}>
            Sign In
          </Link>
        </li>
        <li
          onClick={() => [setActiveBtn(2), setOpen(false)]}
          className={`${styles.option} ${
            activeBtn === 2 ? styles.active : null
          }`}
        >
          <Link to="/signup" className={styles.option}>
            Sign Up
          </Link>
        </li>
        <li
          onClick={() => setActiveBtn(3)}
          className={`${styles.option} ${
            activeBtn === 3 ? styles.active : null
          }`}
        >
          <Link to="/signin" className={styles.option}>
            Register
          </Link>
        </li>
      </ul>

      {renderLogoutOrSigninRegister()}
    </div>
  );
};

export default NavBar;
