import styles from "./Dropdown.module.css";
import React, {
  useState,
  useEffect,
  useRef,
  useContext,
  FC,
  SetStateAction,
  Dispatch,
} from "react";

interface Props {
  open: boolean;
  setOpen: SetStateAction;
  close: boolean;
  setClose: SetStateAction;
}

const Dropdown: FC<Props> = ({ open, setOpen, close, setClose }) => {
  return (
    <div
      className={`${styles.profileDropdownContainer} ${
        open ? styles.active : close ? styles.deactivated : null
      }  `}
    >
      <ul
        className={`${styles.profileDropdownList} ${
          open ? styles.active : null
        }`}
      >
        <li onClick={() => [setOpen(false), setClose(true)]}>
          <Link
            to="/profile"
            className={`${styles.option} ${open ? styles.active : null}`}
          >
            Profile
          </Link>
        </li>
        <li onClick={() => [onLogoutCLick(), setOpen(false), setClose(true)]}>
          <Link
            to="/login"
            className={`${styles.option} ${open ? styles.active : null}`}
          >
            Log Out
          </Link>
        </li>
      </ul>
    </div>
  );
};

export default Dropdown;
