import styles from "./NavBar.module.css";
import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import SideNav from "../SideNav/SideNav";
import logo from "./logo.png";
import profile from "./profile.png";
import { useQuery, useMutation } from "@apollo/client";
import { setCurrentUser } from "../../redux/features/currentUserSlice";
import query from "../../GQL/queries/CurrentUser";
import { useAppSelector, useAppDispatch } from "../../redux/hooks";
import ProfileDropdown from "../ProfileDropdown/ProfileDropdown";

const NavBar = () => {
  let { loading, error, data } = useQuery(query);
  const { pathname } = useLocation();
  const routesToNOTShowUpIn = ["/"];
  const shouldShowUp = !routesToNOTShowUpIn.includes(pathname);
  const [open, setOpen] = useState(false);
  const [activeBtn, setActiveBtn] = useState(0);
  const [user, setUser] = useState("");
  const [isUser, setIsUser] = useState();
  const currentUser = useAppSelector((state) => state.currentUser.value);
  const dispatch = useAppDispatch(false);

  useEffect(() => {
    const fetchAccounts = async () => {
      if (data) {
        dispatch(setCurrentUser(data.user2));
        setIsUser(true);
        // console.log(data);
      } else {
        setIsUser(false);
      }
    };
    fetchAccounts();
  }, [loading, currentUser, data]);

  const signinContent = (
    <li
      onClick={() => setActiveBtn(1)}
      className={`${styles.option} ${activeBtn === 1 ? styles.active : null}`}
    >
      <Link to="/login" className={styles.option}>
        Log In
      </Link>
    </li>
  );
  const registerContent = (
    <li
      onClick={() => setActiveBtn(2)}
      className={`${styles.option} ${activeBtn === 2 ? styles.active : null}`}
    >
      <Link to="/register" className={styles.option}>
        Register
      </Link>
    </li>
  );
  // const logoutContent = (
  //   <li onClick={() => setActiveBtn(3)} className={`${styles.option} `}>
  //     <div className={styles.profilePicContainer}>
  //       <img src={profile} alt="" className={styles.profilePic} />
  //     </div>
  //     <div className={styles.profileDropdownContainer}>
  //       <ul className={styles.profileDropdownList}>
  //         <li>
  //           <Link>Profile</Link>
  //         </li>
  //         <li>
  //           <Link>Log Out</Link>
  //         </li>
  //       </ul>
  //     </div>

  {
    /* <Link to="/landing" className={styles.option}>

      </Link> */
  }
  // </li>
  // );
  /* -------------------------------------------------------------------------- */
  if (shouldShowUp) {
    return (
      <div className={styles.container}>
        {/* -------------------------------- Nav & hamburger ------------------------------- */}
        <SideNav />
        <div className={styles.hamburger} onClick={() => setOpen(!open)}>
          <span className={styles.line}></span>
          <span className={styles.line}></span>
          <span className={styles.line}></span>
        </div>
        {/* ---------------------- Log In and Register OR logout --------------------- */}
        {/* onClick={() => setActiveBtn(1)}
      className={`${styles.option} ${activeBtn === 1 ? styles.active : null}`} */}
        <Link
          to="/"
          className={styles.imageContainer}
          onClick={() => setActiveBtn(0)}
        >
          <img
            src="https://images.vexels.com/media/users/3/142890/isolated/preview/4ea2d7c4bf3cad23a4f18ee58752deb8-high-tech-rings-logo.png"
            alt=""
            className={styles.image}
          />
        </Link>

        <ul className={`${styles.optionList} ${open ? styles.open : null}`}>
          {data && data.user2 ? (
            <ProfileDropdown />
          ) : (
            [signinContent, registerContent]
          )}
        </ul>
      </div>
    );
  } else {
    return null;
  }
};

export default NavBar;
