import React, { useEffect, useState } from "react";
import styles from "./ProfilePic.module.css";
import useIPFS from "../../../utils/useIPFS";

const ProfilePic = () => {
  const { captureFileAndUploadToIPFS } = useIPFS();
  const [showEditContainer, setShowEditContainer] = useState(true);

  return (
    <div
      className={styles.container}
      // onMouseOver={() => setShowEditContainer(true)}
      // onMouseLeave={() => setShowEditContainer(false)}
    >
      <img
        className={styles.pic}
        src="https://static.seattletimes.com/wp-content/uploads/2021/03/robots-2f8da7a6-8bea-11eb-9423-04079921c915-1560x1040.jpg"
        alt=""
      />
      {showEditContainer ? (
        <div className={styles.onHoverContainer}>
          <p className={styles.text}>Edit</p>
        </div>
      ) : null}

      <input
        id="file"
        name="file"
        type="file"
        className={styles.fileButton}
        onChange={(e) => captureFileAndUploadToIPFS(e)}
      />
      <label htmlFor="" for="file">
        buddy
      </label>
    </div>
  );
};

export default ProfilePic;

