import styles from "./MakeComment.module.css";
import React, { useState, useEffect } from "react";
import profilePic from "../../NavBar/profile.png";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";

const MakeComment = ({ commentOpen, setCommentOpen }) => {
  const currentUser = useAppSelector((state) => state.currentUser.value);

  useEffect(() => {
    if (currentUser.friends) {
      // console.log("current User", currentUser.friends);
    }
  }, [currentUser]);

  if (commentOpen) {
    return (
      <div className={styles.container}>
        <form action="" className={styles.formContainer}>
          <div className={styles.profilePicContainer}>
            <img src={profilePic} alt="" className={styles.profilePic} />
          </div>
          <input
            placeholder="Write a comment..."
            type="text"
            className={styles.input}
          />
        </form>
      </div>
    );
  } else {
    return null;
  }
};

export default MakeComment;
