import styles from "./PostList.module.css";
import { useAppSelector, useAppDispatch } from "../../../../redux/hooks";
import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../../GQL/queries/CurrentUser";
import Post from "../Post/Post";

const PostList = () => {
  const currentUser = useAppSelector((state) => state.currentUser.value);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");
  const [friendList, setFriendList] = useState("");
  const [postList, setPostList] = useState([]);
  const [postListDone, setPostListDone] = useState([]);
  const [posts, setPosts] = useState("");

  useEffect(() => {
    if (currentUser.friends) {
      setFriendList(currentUser.friends);
      // console.log("current User", currentUser.friends);
    }
  }, [currentUser]);

  /* ------------------------- creating list of posts ------------------------- */
  useEffect(() => {
    if (friendList) {
      const newList = friendList.map((friend, index) => {
        return friend.posts;
      });

      const finishedFriendList = newList.flat();

      const completeListOfMyPostsAndFriendPosts =
        currentUser.posts.concat(finishedFriendList);

      setPostList(completeListOfMyPostsAndFriendPosts);
    }
  }, [friendList, currentUser]);

  /* -------------------------- sorting list of posts ------------------------- */
  useEffect(() => {
    if (postList.length > 0) {
      setPostListDone(postList.sort((a, b) => (a.time > b.time ? -1 : 1)));
    }
  }, [postList]);

  const renderPosts = () => {
    return postListDone.map((post, index) => {
      const d = new Date(parseInt(post.time));
      const date = d.toString();
      console.log(post, "post test");

      return (
        <div key={index}>
          <Post
            name={post.userId.name}
            date={date}
            postId={post.id}
            postContent={post.post}
            likeCount={post.likedBy.length}
            likeList={post.likedBy}
            commentCount={post.comments.length}
            commentList={post.comments}
          />
        </div>
      );
    });
  };

  return <div className={styles.postListContainer}>{renderPosts()}</div>;
};

export default PostList;
