import styles from "./NavBar.module.css";
import React, { useState, useEffect, useRef } from "react";
import SideNav from "../SideNav/SideNav";
import { useQuery, useMutation } from "@apollo/client";
import { setCurrentUser } from "../../../redux/features/currentUserSlice";
import currentUserQuery from "../../../GQL/queries/CurrentUser";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import ProfileDropdown from "../ProfileDropdown/ProfileDropdown";
import { Link, useLocation } from "react-router-dom";
import mutation from "../../../GQL/mutations/Logout";

const NavBar = () => {
  const wrapperRef = useRef(null);
  let [logout] = useMutation(mutation);
  let { loading, error, data } = useQuery(currentUserQuery);
  const [isUser, setIsUser] = useState();
  const currentUser = useAppSelector((state) => state.currentUser);
  const dispatch = useAppDispatch(false);
  const [open, setOpen] = useState(false);
  const [close, setClose] = useState(false);

  useEffect(() => {
    document.addEventListener("click", handleClickOutside, false);
    return () => {
      document.removeEventListener("click", handleClickOutside, false);
    };
  }, []);
  /* -------------------------------------------------------------------------- */
  useEffect(() => {
    const fetchAccounts = () => {
      if (data) {
        dispatch(setCurrentUser(data.user2));
        setIsUser(true);
        // console.log(currentUser);
      } else {
        console.log("bro");
        setIsUser(false); //?????
      }
    };
    fetchAccounts();
  }, [data, currentUser]);

  /* -------------------------------------------------------------------------- */
  useEffect(() => {
    // console.log(currentUser);
  }, [setIsUser]);

  const onLogoutCLick = () => {
    logout({
      refetchQueries: [{ query: currentUserQuery }],
      awaitRefetchQueries: true,
    }).catch((err) => console.log(err));
  };
  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setOpen(false);
    }
  };
  return (
    <div className={styles.container0}>
      <div className={styles.container}>
        <SideNav />
        <ProfileDropdown />
      </div>
      <div
        className={`${styles.profileDropdownContainer} ${
          open ? styles.active : close ? styles.deactivated : null
        }  `}
      >
        <ul
          className={`${styles.profileDropdownList} ${
            open ? styles.active : null
          }`}
        >
          <li onClick={() => [setOpen(false), setClose(true)]}>
            <Link
              to="/profile"
              className={`${styles.option} ${open ? styles.active : null}`}
            >
              Profile
            </Link>
          </li>
          <li onClick={() => [onLogoutCLick(), setOpen(false), setClose(true)]}>
            <Link
              to="/login"
              className={`${styles.option} ${open ? styles.active : null}`}
            >
              Log Out
            </Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default NavBar;
