import styles from "./MakePost.module.css";
import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../GQL/queries/CurrentUser";
import profilePic from "./profile.png";
import PostForm from "../PostForm/PostForm";

const MakePost = () => {
  let { loading, error, data } = useQuery(getCurrentUser);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (data) {
      const user = data.user2;
      setUser(user);
      // console.log(user);
    }
  }, [data]);

  /* -------------------------------------------------------------------------- */
  const onSubmit = (e) => {
    e.preventDefault();
    if (text.length > 0) {
      editUser({
        variables: { id: user.id, addPost: { post: text } },
        refetchQueries: [{ query: getCurrentUser }],
        awaitRefetchQueries: true,
      })
        .then(
          (res) => null
          // console.log("yayy")
        )
        //   .then((res) => [setAgeText(""), setEmailText(""), setNameText("")])
        .catch((err) => {
          const errors = err.graphQLErrors?.map((error) => error.message);

          console.log(errors);
        });
    }
  };

  return (
    <div className={styles.container}>
      <form action="" onSubmit={onSubmit} className={styles.picInputContainer}>
        <div className={styles.picInputContainer}>
          <img src={profilePic} alt="" className={styles.profilePic} />
          <div
            className={styles.input}
            onClick={() => setShowModal(!showModal)}
          >
            What's on your mind,name here
          </div>
        </div>
        {/* <button> hi</button> */}
      </form>
      <PostForm showModal={showModal} setShowModal={setShowModal} />
    </div>
  );
};

export default MakePost;
