import styles from "./ProfileDropdown.module.css";
import React, { useState, useEffect, useRef } from "react";
import profile from "./profile.png";
import { Link, useLocation } from "react-router-dom";

const ProfileDropdown = () => {
  const { pathname } = useLocation();
  const [open, setOpen] = useState(false);
  const [close, setClose] = useState(false);
  const [currentPath, setCurrentPath] = useState(pathname);

  // useEffect(() => {
  //   setCurrentPath(pathname);
  // }, []);

  useEffect(() => {
    if (open) {
      // setOpen(false);
      // setClose(true);
    }
  }, [pathname]);

  useEffect(() => {
    document.addEventListener("click", handleClickOutside, false);
    return () => {
      document.removeEventListener("click", handleClickOutside, false);
    };
  }, []);

  const wrapperRef = useRef(null);
  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setOpen(false);
      // setClose(true);
    }
  };

  return (
    <div
      className={styles.container}
      ref={wrapperRef}
      onMouseLeave={() => [setOpen(false), setClose(true)]}
    >
      <li className={`${styles.option} `}>
        <div className={styles.profilePicContainer}>
          <img
            src={profile}
            alt=""
            className={styles.profilePic}
            onMouseOver={() =>
              // !open
              //   ? [setOpen(true), setClose(false)]
              //   : [setOpen(false), setClose(true)]
              setOpen(true)
            }
            // onMouseLeave={() => setOpen(false)}
          />
        </div>

        <div
          // onMouseLeave={() => setOpen(false)}
          className={`${styles.profileDropdownContainer} ${
            open
              ? // && currentPath === pathname
                styles.active
              : close
              ? styles.deactivated
              : null
          }  `}
        >
          <ul
            className={`${styles.profileDropdownList} ${
              open ? styles.active : null
            }`}
          >
            <li onClick={() => [setOpen(false), setClose(true)]}>
              <Link
                to="/profile"
                className={`${styles.option} ${open ? styles.active : null}`}
              >
                Profile
              </Link>
            </li>
            <li onClick={() => [setOpen(false), setClose(true)]}>
              <Link
                to="/"
                className={`${styles.option} ${open ? styles.active : null}`}
              >
                Log Out
              </Link>
            </li>
          </ul>
        </div>

        {/* <Link to="/landing" className={styles.option}>

      </Link> */}
      </li>
    </div>
  );
};

export default ProfileDropdown;
