import styles from "./ChatBox.module.css";
import React, { useState, useEffect, useRef } from "react";
import useChatRoom from "../useChatRoom";
import useMessages from "../useMessages";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";

const ChatBox = () => {
  const messageRef = useRef();
  const { messageList, addMessage } = useMessages();
  const { messages, sendMessage } = useChatRoom(messageList);
  // const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [roomText, setRoomText] = useState("");
  const room = useAppSelector((state) => state.currentRoom.value);

  useEffect(() => messageRef.current.scrollIntoView({ behavior: "smooth" }));
  const renderMessages = () => {
    return messages.map((msg, index) => {
      return (
        <p
          key={index}
          className={`${msg.isOwner ? styles.blue : styles.red} 
          ${styles.msg}`}
        >
          {msg.body}
        </p>
      );
    });
  };
  const onSubmit = (e) => {
    e.preventDefault();
    if (newMessage !== "") {
      sendMessage(newMessage);
    }
    // addMessage()
    console.log("hi");
    //   setMessages([...messages, newMessage]);
    setNewMessage("");
  };
  /* -------------------------------------------------------------------------- */
  return (
    <div className={styles.container}>
      <div className={styles.friendNameContainer}>
        <h1>Friend Name</h1>
      </div>
      <div className={styles.messageContainer}>
        {room === "" ? (
          <div className={styles.noRoomMessageContainer}>
            Please Select a <br /> Room To Join
            <div ref={messageRef}>{/* message ref */}</div>
          </div>
        ) : (
          <div>
            <div className={styles.messageList}>
              {/* messages here */}
              {renderMessages()}
              <div ref={messageRef}>{/* message ref */}</div>
            </div>
            <div className={styles.inputBtn}>
              <form action="" onSubmit={onSubmit}>
                <input
                  type="text"
                  name=""
                  id=""
                  placeholder="Start Chatting"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                />
                <button>Send</button>
              </form>
            </div>
          </div>
        )}

        {/* -------------------------------------------------------------------------- */}
      </div>
    </div>
  );
};

export default ChatBox;
