import styles from "./RegisterForm.module.css";
import React, { useState, useEffect } from "react";
import { gql, useMutation, useQuery } from "@apollo/client";
import axios from "axios";
import mutation from "../../GQL/mutations/Signup";
import query from "../../GQL/queries/CurrentUser";
import history from "../../utils/history";

const RegisterForm = () => {
  const [errors, setErrors] = useState([]);
  const [signup] = useMutation(mutation);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    signup({
      variables: { email: email, password: password },
      refetchQueries: [{ query: query }],
      awaitRefetchQueries: true,
    })
      .then((e) => (e ? history.push("/home") : null))
      .catch((err) => {
        const errors = err.graphQLErrors?.map((error) => error.message);
        setErrors(errors);
        console.log(errors);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        {/* <div className={styles.title}>
          <h1>Register</h1>
        </div> */}
        <form action="" className={styles.form} onSubmit={(e) => onSubmit(e)}>
          <label htmlFor="">First Name</label>
          <input type="text" />
          <label htmlFor="">Last Name</label>
          <input type="text" />
          <label htmlFor="">Email</label>
          <input type="text" onChange={(e) => setEmail(e.target.value)} />
          <label htmlFor="">Password</label>
          <input type="text" onChange={(e) => setPassword(e.target.value)} />
          <div className={styles.btnContainer}>
            <button className={`${styles.button} ${styles.login}`}>
              Log In
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegisterForm;
