import styles from "./LogInForm.module.css";
import React, { useState, useEffect } from "react";
import { useMutation } from "@apollo/client";
import mutation from "../../GQL/mutations/Login";
import query from "../../GQL/queries/CurrentUser";
import history from "../../utils/history";

const LogInForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [data, setData] = useState(null);
  const [errors, setErrors] = useState([]);
  const [login] = useMutation(mutation);

  const onSubmit = (e) => {
    e.preventDefault();
    login({
      variables: { email: email, password: password },
      refetchQueries: [{ query: query }],
      awaitRefetchQueries: true,
    })
      .then(setEmail(""), setPassword(""))
      .then((e) => (e ? history.push("/home") : null))
      .catch((err) => {
        const errors = err.graphQLErrors?.map((error) => error.message);
        setErrors(errors);
        console.log(errors);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <div className={styles.title}>
          <h1>Login</h1>
        </div>
        <form action="" className={styles.form} onSubmit={onSubmit}>
          {/* <label htmlFor="">Email</label> */}
          <input
            placeholder="Email"
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {/* <label htmlFor="">Password</label> */}
          <input
            placeholder="Password"
            type="text"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className={`${styles.button} ${styles.login}`}>Submit</button>
        </form>
        <hr />
        <button className={`${styles.button} ${styles.login}`}>
          Create new account
        </button>
      </div>
    </div>
  );
};

export default LogInForm;
