import React, {
  useState,
  useEffect,
  useRef,
  useContext,
  FC,
  SetStateAction,
  Dispatch,
} from "react";
import profile from "./profile.png";
import Dropdown from "../Dropdown/Dropdown";

const ProfileDropdownInitiator = ({
  open,
  setOpen,
  close,
  setClose,
  onDropDown,
  setOnDropDown,
}) => {
  const wrapperRef = useRef(null);

  /* -------------------------------------------------------------------------- */
  return (
    // <div
    //   className={styles.container}
    //   ref={wrapperRef}

    // >
    <div
      className={styles.profilePicContainer}
      onMouseLeave={() =>
        onDropDown === false ? [setOpen(false), setClose(true)] : null
      }
    >
      <img
        src={profile}
        alt=""
        className={styles.profilePic}
        // onMouseOver={() => [setOpen(true), setOnDropDown(true)]}
      />
      <div
        className={styles.dropDownDecoy}
        onMouseLeave={() => [setOpen(false), setClose(true)]}
      >
        <div className={styles.imageDecoy}></div>
      </div>
    </div>
    // </div>
  );
};

export default ProfileDropdownInitiator;
