import styles from "./PostList.module.css";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../GQL/queries/CurrentUser";
import Post from "../Post/Post";

const PostList = () => {
  // let { loading, error, data } = useQuery(getCurrentUser);
  const currentUser = useAppSelector((state) => state.currentUser.value);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");
  const [friendList, setFriendList] = useState("");
  const [postList, setPostList] = useState([]);

  const [posts, setPosts] = useState("");

  useEffect(() => {
    if (currentUser.friends) {
      setFriendList(currentUser.friends);
      console.log("hi", currentUser.friends);
    }
  }, [currentUser]);
  useEffect(() => {
    if (friendList) {
      friendList.map((friend, index) => {
        // console.log(friend.posts);
        // const nameIncludedList = { name: friend.name, ...friend.posts };
        // console.log(nameIncludedList, "name list");
        // console.log(friend.posts, "kkkkk");
        setPostList(postList.concat(friend.posts));
        return null;
      });
    }
  }, [friendList]);

  useEffect(() => {
    if (postList) {
      console.log(postList);
      postList.sort((a, b) => (a.time > b.time ? -1 : 1));
      console.log("2", postList);
    }
  }, [postList]);

  // const renderPosts = () => {
  //   return posts.map((post, index) => {
  //     return (
  //       <div key={index} className={styles.individualPost}>
  //         <h3>{user.email}</h3> edit post/delete post here <button>Edit</button>{" "}
  //         time/date here
  //         <h2>{post.post}</h2>
  //         <button>Like</button>
  //         <hr />
  //         comments//// <button>Comment</button>
  //         <br />
  //         post.comments here
  //       </div>
  //     );
  //   });
  // };
  const renderPosts = () => {
    console.log(postList, "done");
    return postList.map((post, index) => {
      const d = new Date(parseInt(post.time));
      const date = d.toString();
      console.log(typeof date.toString());

      return (
        <div key={index}>
          <Post
            name={post.userId.name}
            date={date}
            postId={post.id}
            postContent={post.post}
            likeCount={post.likedBy.length}
            commentCount={post.comments.length}
          />
        </div>
      );
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.postListContainer}>{renderPosts()}</div>
    </div>
  );
};

export default PostList;
