import styles from "./MakeComment.module.css";
import React, { useState, useEffect } from "react";
import profilePic from "../../NavBar/profile.png";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import { useMutation, useQuery } from "@apollo/client";
import editUserMutation from "../../../GQL/mutations/EditUser";
import currentUserQuery from "../../../GQL/queries/CurrentUser";

const MakeComment = ({ commentOpen, setCommentOpen }) => {
  const [commentText, setCommentText] = useState("");
  const [editUser] = useMutation(editUserMutation);
  const currentUser = useAppSelector((state) => state.currentUser.value);

  useEffect(() => {
    if (currentUser.friends) {
      // console.log("current User", currentUser.friends);
    }
  }, [currentUser]);

  const onSubmit = (e) => {
    e.preventDefault();
    commentText.length > 0
      ? addPost({
          variables: {
            userId: currentUser.id,
            post: commentText,
          },
          refetchQueries: [{ query: currentUserQuery }],
          awaitRefetchQueries: true,
        })
          .then((res) =>
            // console.log(res.data.addPost.id)
            editUser({
              variables: {
                id: currentUser.id,
                addPost: res.data.addPost.id,
              },
              refetchQueries: [{ query: currentUserQuery }],
              awaitRefetchQueries: true,
            })
              .catch((err) => {
                const errors = err.graphQLErrors?.map((error) => error.message);

                console.log(errors);
              })
              .then(setShowModal(!showModal))
          )
          .catch((err) => {
            const errors = err.graphQLErrors?.map((error) => error.message);
            console.log(errors);
          })
      : console.log("need to have post greater than zero length");
  };

  if (commentOpen) {
    return (
      <div className={styles.container}>
        <form action="" className={styles.formContainer}>
          <div className={styles.profilePicContainer}>
            <img src={profilePic} alt="" className={styles.profilePic} />
          </div>
          <textarea
            onChange={(e) => setCommentText(e.target.value)}
            className={styles.input}
            placeholder="What's on your mind, Omar?"
          ></textarea>
        </form>
      </div>
    );
  } else {
    return null;
  }
};

export default MakeComment;
