//     console.log(errors);
//   });
// const selectFriend = () => {
//   //   const { combineAddresses } = useChatRoom();
//   return "1234";
// };

const useMessages = () => {
  let { loading, error, data } = useQuery(query);
  // const [convoId, setConvoId] = useState(null);
  const room = useAppSelector((state) => state.currentRoom.value);
  let {
    loading: loadingMessages,
    error: errorMessages,
    data: dataMessages,
    refetch: refetchMessages,
  } = useQuery(getMessagesQuery, {
    variables: { convoId: room },
  });
  const [user, setUser] = useState(null);
  const [messageList, setMessageList] = useState([]);
  const [selectedFriend, setSelectedFriend] = useState(null);

  const [addConversation] = useMutation(addConversationMutation);
  const [editMessage] = useMutation(editMessageMutation);
  const dispatch = useAppDispatch();
  //   let {
  //     loading: messagesLoading,
  //     error: messagesError,
  //     data: messagesData,
  //   } = useQuery(getMessagesQuery, {
  //     variables: { convoId: "1234" },
  //   });

  let [
    getCurrentFriendMessages,
    {
      loading: messagesLoading,
      error: messagesError,
      data: messagesData,
      refetch: messageRefetch,
    },
  ] = useLazyQuery(getMessagesQuery, {
    variables: { convoId: room },
  });

  /* -------------------------------------------------------------------------- */

  useEffect(() => {
    if (data) {
      const user = data.user2;
      setUser(user);
    }
    if (!messagesError && messagesData) {
      dispatch(setPreviousMessages([]));
      if (messagesData.messages[0] !== undefined) {
        console.log(messagesData.messages);
        const alteredMessageList = [];

        messagesData.messages[0].messages.map((msg) => {
          const newMessage = { body: "", isOwner: false };
          if (msg.senderId === user.id) {
            newMessage.isOwner = true;
          }
          newMessage.body = msg.body;
          // console.log(msg);
          // console.log(msg.body);
          // console.log(msg.senderId);
          alteredMessageList.push(newMessage);
          return null;
        });

        setMessageList(alteredMessageList);
        //   console.log("message", alteredMessageList);
        dispatch(setPreviousMessages(alteredMessageList));
      }
    } else {
      //   setMessageList([]);
      //   console.log("message", alteredMessageList);
      //   dispatch(setPreviousMessages([]));
      return null;
    }
  }, [data, messagesData, messagesLoading, user]);

  /* -------------------------------------------------------------------------- */
  const addMessage = (convoId, body) => {
    editMessage({
      variables: {
        convoId: convoId,
        addMessage: { senderId: user.id, body: body },
      },
      refetchQueries: [
        {
          getMessagesQuery: getMessagesQuery,
        },
      ],
      awaitRefetchQueries: true,
    })
      .then((res) => refetchMessages())
      //   .then((res) => console.log(res.data.editMessage.messages))
      .catch((err) => {
        const errors = err.graphQLErrors?.map((error) => error.message);
        console.log(errors);
      })
      .then((res) => setMessageList());
  };
  /* -------------------------------------------------------------------------- */
  const alteredChatData = async () => {
    if (messageList.length > 0) {
      const newMessageList = [];
      messageList.map((msg) => {
        const newMessage = { body: "", isOwner: false };
        if (msg.senderId === user.id) {
          newMessage.isOwner = true;
        }
        newMessage.body = msg.body;
        // console.log(msg.body);
        // console.log(msg.senderId);

        return newMessageList.push(newMessage);
      });
      console.log(newMessageList);
      return newMessageList;
    }
  };

  /* -------------------------------------------------------------------------- */

  /* -------------------------------------------------------------------------- */

  /* -------------------------------------------------------------------------- */
  //   if (messageList) {
  return {
    messageList,
    addMessage,
    user,
    getCurrentFriendMessages,
    selectedFriend,
    setSelectedFriend,
    setMessageList,
  };
  //   }
};

export default useMessages;
