import styles from "./MakePost.module.css";
import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../GQL/queries/CurrentUser";

const MakePost = () => {
  let { loading, error, data } = useQuery(getCurrentUser);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");

  useEffect(() => {
    if (data) {
      const user = data.user2;
      setUser(user);
      console.log(user);
    }
  }, [data]);

  /* -------------------------------------------------------------------------- */
  const onSubmit = (e) => {
    e.preventDefault();
    if (text.length > 0) {
      editUser({
        variables: { id: user.id, addPost: { post: text } },
        refetchQueries: [{ query: getCurrentUser }],
        awaitRefetchQueries: true,
      })
        .then((res) => console.log("yayy"))
        //   .then((res) => [setAgeText(""), setEmailText(""), setNameText("")])
        .catch((err) => {
          const errors = err.graphQLErrors?.map((error) => error.message);

          console.log(errors);
        });
    }
  };

  return (
    <div className={styles.container}>
      <form action="" onSubmit={onSubmit}>
        <label htmlFor=""> post</label>
        <input
          placeholder="What's on your mind,name here"
          type="text"
          name=""
          id=""
          onChange={(e) => setText(e.target.value)}
        />
        <button> hi</button>
      </form>
    </div>
  );
};

export default MakePost;
