import styles from "./RegisterForm.module.css";
import React, { useState, useEffect } from "react";
import { gql, useMutation, useQuery } from "@apollo/client";
import axios from "axios";
import mutation from "../../GQL/mutations/Signup";
import query from "../../GQL/queries/CurrentUser";
import history from "../../utils/history";

const RegisterForm = ({ setShowModal, showModal }) => {
  const [errors, setErrors] = useState([]);
  const [signup] = useMutation(mutation);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    signup({
      variables: { email: email, password: password },
      refetchQueries: [{ query: query }],
      awaitRefetchQueries: true,
    })
      .then((e) => (e ? history.push("/home") : null))
      .catch((err) => {
        const errors = err.graphQLErrors?.map((error) => error.message);
        setErrors(errors);
        console.log(errors);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <img
          className={styles.x}
          src="https://static.xx.fbcdn.net/rsrc.php/v3/y2/r/__geKiQnSG-.png"
          alt=""
          width="24"
          height="24"
        />
        <div className={styles.titleContainer}>
          <h1>Sign Up</h1>

          <p>It's quick and easy.</p>
          <hr className={styles.horizontalLine} />
        </div>

        <form action="" className={styles.form} onSubmit={(e) => onSubmit(e)}>
          <div className={styles.sameLineInputContainer}>
            <input
              type="text"
              placeholder="First Name"
              className={styles.sameLineInput}
            />

            <input
              type="text"
              placeholder="Last Name"
              className={styles.sameLineInput}
            />
          </div>

          <input
            type="text"
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
            className={styles.inputItem}
          />

          <input
            placeholder="New Password"
            type="text"
            onChange={(e) => setPassword(e.target.value)}
            className={styles.inputItem}
          />
          <div className={styles.btnContainer}>
            <button className={`${styles.button} ${styles.signUp}`}>
              Sign Up
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegisterForm;
