import styles from "./EditDeleteComment.module.css";
import React, { useState, useEffect, useRef } from "react";
import { Modal } from "./modal/modal";
import { useAppSelector, useAppDispatch } from "../../../../redux/hooks";
import X_SVG from "./x.svg";

const EditDeleteComment = ({ comment }) => {
  const currentUser = useAppSelector((state) => state.currentUser);
  const [showModal, setShowModal] = useState(false);
  const [commentText, setCommentText] = useState(comment);

  const editComment = () => {};
  const deleteComment = () => {};

  if (!showModal) return <div onClick={() => setShowModal(true)}>...</div>;

  return (
    <Modal showModal={showModal} setShowModal={setShowModal}>
      <div
        className={styles.container}
        // ref={modal}
      >
        <div className={styles.createPostContainer}>
          <div className={styles.titleContainer}>
            <h2>Edit Comment</h2>
            <img
              className={styles.x_svg}
              src={X_SVG}
              alt="exit"
              onClick={() => setShowModal(!showModal)}
            />
          </div>
        </div>

        <form action="" onSubmit={(e) => onSubmit(e)}>
          <div className={styles.contentContainer}>
            <div className={styles.inputContainer}>
              <textarea
                // onChange={(e) => setPostText(e.target.value)}
                className={styles.input}
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                placeholder={`What's on your mind, ${currentUser.name}?`}
                // maxlength="1"
              ></textarea>
            </div>
            <div className={styles.postBtnContainer}>
              <button className={`${styles.editBtn} ${styles.btn}`}>
                Edit
              </button>
              <button className={`${styles.deleteBtn} ${styles.btn}`}>
                Delete
              </button>
            </div>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default EditDeleteComment;
