import styles from "./NavBar.module.css";
import React, { useState, useEffect, useRef } from "react";
import SideNav from "../SideNav/SideNav";
import { useQuery, useMutation } from "@apollo/client";
import { setCurrentUser } from "../../../redux/features/currentUserSlice";
import { setOnlineUsers } from "../../../redux/features/onlineUsersSlice";
import currentUserQuery from "../../../GQL/queries/CurrentUser";
import { setSocket } from "../../../redux/features/socketSlice";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import { Link, useLocation } from "react-router-dom";
import mutation from "../../../GQL/mutations/Logout";
import ProfileDropdownInitiator from "../ProfileDropdownInitiator/ProfileDropdownInitiator";
import TestDropDown from "../TestDropDown/TestDropDown";
import socketIOClient from "socket.io-client";
const SOCKET_SERVER_URL = "http://localhost:4000";

const NavBar = () => {
  // const { pathname } = useLocation();
  // const dontShowUp = ["/", "/login"];
  // const includedPath = !dontShowUp.includes(pathname);

  let { loading, error, data, refetch } = useQuery(currentUserQuery);

  const currentUser = useAppSelector((state) => state.currentUser);

  const dispatch = useAppDispatch(false);
  const [open, setOpen] = useState(false);
  const [close, setClose] = useState(false);
  const [onDropDown, setOnDropDown] = useState(false);
  const [ableToSetUserOnline, setAbleToSetUserOnline] = useState(false);
  const [socketIdForLogoutset, setSocketIdForLogout] = useState(null);
  const [socketActive, setSocketActive] = useState(false);
  const socketRef = useRef();

  /* -------------------------------------------------------------------------- */
  useEffect(() => {
    if (data) {
      console.log("DATAAAA", data.user2);
    }
    if (data) {
      if (socketRef !== undefined && !data.user2) {
        socketRef.current.disconnect();
        console.log("BOOM");
      }
    }
  }, [data]);
  useEffect(() => {
    if (data) {
      socketRef.current = socketIOClient(SOCKET_SERVER_URL);
      setSocketActive(true);
    }
  }, [data]);
  useEffect(() => {
    refetch();

    socketRef.current = socketIOClient(SOCKET_SERVER_URL);
    // if (data) {
    //   // socketRef.current.emit("login", { userId: data.user2.id });
    // } else {
    //   socketRef.current.disconnect();
    // }

    if (data && data.user2 && socketActive) {
      socketRef.current.emit("login", { userId: data.user2.id });
      // console.log(data.user2.id, "NavBar");
      dispatch(setCurrentUser(data.user2));
      setAbleToSetUserOnline(true);

      setOpen(!open);
      console.log("done");
    }
  }, [data, socketActive]);

  useEffect(() => {
    if (ableToSetUserOnline && data.user2) {
      // dispatch(setSocket(socketRef));
      // console.log("USER ID FRONT END", data.user2.id);
      socketRef.current.emit("login", { userId: data.user2.id });

      socketRef.current.on("onlineUsers", (data) => {
        console.log("socket if font end", socketRef.current.id);
        setSocketIdForLogout(socketRef.current.id);
        console.log("hehehe", data);
        const onlineUsers = Object.entries(data);
        const onlineIds = Array.from(onlineUsers, (x) => x[1]);
        console.log(onlineIds);
        dispatch(setOnlineUsers(onlineIds));
      });
      return () => {
        // socketRef.current.disconnect();
      };
    }
  }, [ableToSetUserOnline]);

  // useEffect(() => {
  //   if (currentUser.id !== null) {
  //     // console.log(data.user2.id);
  //     socketRef.current.emit("login", { userId: currentUser.id });

  //     socketRef.current.on("onlineUsers", (data) => {
  //       console.log("hehehe", data);
  //     });
  //   }
  // }, []);

  /* -------------------------------------------------------------------------- */

  // if (includedPath) {
  return (
    <div className={styles.container0}>
      <div className={styles.container}>
        <SideNav />
        <ProfileDropdownInitiator />
        {/* <TestDropDown /> */}
      </div>
      <div className={styles.container69}>
        <SideNav />
        <TestDropDown
          socketRef={socketRef}
          socketIdFromNav={socketIdForLogoutset}
        />
      </div>

      {/* <div className={styles.test}>
        <TestDropDown />
      </div> */}
    </div>
  );
  // }
  //  else {
  //   return null;
  // }
};

export default NavBar;
