import styles from "./ProfileDropdownInitiator.module.css";
import React, {
  useState,
  useEffect,
  useRef,
  useContext,
  FC,
  SetStateAction,
  Dispatch,
} from "react";
import profile from "./profile.png";
import Dropdown from "../Dropdown/Dropdown";

const ProfileDropdownInitiator = ({ open, setOpen, close, setClose }) => {
  const wrapperRef = useRef(null);

  /* -------------------------------------------------------------------------- */
  return (
    // <div
    //   className={styles.container}
    //   ref={wrapperRef}

    // >
    <div
      className={styles.profilePicContainer}
      onMouseLeave={() => [setOpen(false), setClose(true)]}
    >
      <img
        src={profile}
        alt=""
        className={styles.profilePic}
        onMouseOver={() => setOpen(true)}
      />
      <div
        className={`${styles.profileDropdownContainer} ${
          open ? styles.active : close ? styles.deactivated : null
        }  `}
      >
        <ul
          className={`${styles.profileDropdownList} ${
            open ? styles.active : null
          }`}
        >
          <li onClick={() => [setOpen(false), setClose(true)]}>
            <Link
              to="/profile"
              className={`${styles.option} ${open ? styles.active : null}`}
            >
              Profile
            </Link>
          </li>
          <li onClick={() => [onLogoutCLick(), setOpen(false), setClose(true)]}>
            <Link
              to="/login"
              className={`${styles.option} ${open ? styles.active : null}`}
            >
              Log Out
            </Link>
          </li>
        </ul>
        hi
      </div>
    </div>
    // </div>
  );
};

export default ProfileDropdownInitiator;
