import styles from "./PostList.module.css";

import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../GQL/queries/CurrentUser";

const PostList = () => {
  let { loading, error, data } = useQuery(getCurrentUser);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");
  const [posts, setPosts] = useState("");

  useEffect(() => {
    if (data) {
      const user = data.user2;
      setUser(user);
    }
  }, [data]);
  useEffect(() => {
    if (user) {
      console.log(user.posts);
      setPosts(user.posts);
    }
  }, [user]);

  const renderPosts = () => {
    return posts.map((post, index) => {
      return (
        <div key={index} className={styles.individualPost}>
          <h3>{user.email}</h3> edit post/delete post here <button>Edit</button>{" "}
          time/date here
          <h2>{post.post}</h2>
          <button>Like</button>
          <hr />
          comments//// <button>Comment</button>
          <br />
          post.comments here
        </div>
      );
    });
  };
  return (
    <div className={styles.container}>
      <div className={styles.postListContainer}>
        {/* {posts ? renderPosts() : null} */}
        <div className={styles.individualPost}>
          <div className={styles.name_edit_Container}>
            <div>
              <h3 className={styles.name}>Omar Ragab</h3>{" "}
              <div>Dec 13 at 10:24 AM</div>
            </div>
            {/* <div className={styles.edit}>edit post/delete post here</div> */}
            <div className={styles.editDots}>...</div>
          </div>
          <div className={styles.post_likes_Container}>
            <h2 className={styles.post}>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Magnam
              placeat, deserunt expedita obcaecati neque assumenda aspernatur
              architecto molestias soluta nulla sapiente, voluptatibus
              voluptates beatae minima! Non fugiat quaerat provident officia.
            </h2>
            <p className={styles.postLikes}>like Count:0</p>
          </div>
          <hr className={styles.hr} />
          <div className={styles.like_comment_btn_container}>
            <div className={styles.likeBtn}>Like</div>
            <div className={styles.commentBtn}>Comment</div>
          </div>
          <hr className={styles.hr} />
          comments//// <button>Comment</button>
          <br />
          post.comments here
        </div>
      </div>
    </div>
  );
};

export default PostList;
