import React, { useState, useEffect, useRef } from "react";
import ReactDOM from "react-dom";
import RegisterForm from "../../RegisterForm/RegisterForm";
import styles from "./modal.module.css";
import { gsap } from "gsap";
// import history from "../../history";

export const Modal = ({ content, showModal, Open, setShowModal }) => {
  let textContainer = useRef();
  let loginFormContainer = useRef();

  useEffect(() => {
    const { width, height } = window;
    console.log(width);
    gsap.from(textContainer.current, { x: -140, opacity: 0, duration: 0.7 });
    gsap.from(loginFormContainer.current, { x: 0, opacity: 0, duration: 1.6 });
  });
  if (showModal) {
    return ReactDOM.createPortal(
      <div // background
        // onClick={() => setShowModal(!showModal)}
        className={styles.modal_background}
      >
        <div // Modal
          onClick={(e) => e.stopPropagation()}
          className={styles.modal}
        >
          <RegisterForm setShowModal={setShowModal} showModal={showModal} />
        </div>
      </div>,
      document.querySelector("#modal")
    );
  } else return <></>;
};
