import React, { useEffect, useState } from "react";
import { useMutation, useQuery, useLazyQuery } from "@apollo/client";
import getUsers from "../../GQL/queries/getUsers";
import styles from "./AddFriends.module.css";
import mutation from "../../GQL/mutations/EditUser";
import currentUserQuery from "../../GQL/queries/CurrentUser";

const AddFriends = () => {
  let { loading, error, data } = useQuery(getUsers);
  let {
    loading: currentUserLoading,
    errorcurrentUserError,
    data: currentUserData,
  } = useQuery(currentUserQuery);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [friends, setFriends] = useState([]);

  useEffect(() => {
    if (data) {
      // console.log(data);
    }
    if (currentUserData) {
      setFriends([]);
      const currentUser = currentUserData.user2;
      // console.log("HELP", currentUser.friends);
      setUser(currentUser);

      currentUser.friends.map((friend, index) => {
        if (friend.id) {
          setFriends([...friends, friend.id]);
        }
        return null;
      });
    }
  }, [currentUserData]);

  useEffect(() => {
    if (currentUserData) {
      // console.log(currentUserData.user2.friends);
      currentUserData.user2.friends.map(() => {});
    }
  });

  const addFriend = async (friendId) => {
    const friendIdList = [];

    user.friends.map((friend, index) => {
      if (friend.id) {
        friendIdList.push(friend.id);
      }
      return null;
    });

    console.log(user.friends);
    const friend = friendIdList.includes(friendId);
    const me = friendIdList.includes(user.id);
    console.log("friend", friend);
    console.log("me", me);
    console.log(user.friends.id);
    if (friends) {
      if (friend) {
        console.log("HELLLOOOOO");
        console.log("already a friend");
        return false;
      } else {
        editUser({
          variables: {
            id: user.id,
            addFriends: friendId,
          },
          refetchQueries: [{ query: currentUserQuery }],
          awaitRefetchQueries: true,
        }).catch((err) => {
          const errors = err.graphQLErrors?.map((error) => error.message);

          console.log(errors);
        });
      }
    }
  };

  const renderAvaliableUsers = () => {
    return data.users.map((user, index) => {
      const accountStart = user.id.slice(0, 6);
      const accountEnd = user.id.slice(-4);
      return (
        <div key={index} className={styles.userCard}>
          <h1>{user.name}</h1>
          <h3>{user.email}</h3>
          <button onClick={() => addFriend(user.id)}>Add Friend</button>
        </div>
      );
    });
  };
  return (
    <div className={styles.container}>
      <div className={styles.cardsContainer}>
        {data ? renderAvaliableUsers() : null}
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
        <div className={styles.userCard}>hi</div>
      </div>
    </div>
  );
};

export default AddFriends;
