import styles from "./NavBar.module.css";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import SideNav from "../SideNav/SideNav";
import logo from "./logo.png";
import profile from "./profile.png";

const NavBar = () => {
  const [open, setOpen] = useState(false);
  const [activeBtn, setActiveBtn] = useState(0);
  const [user, setUser] = useState("");

  const signinContent = (
    <li
      onClick={() => setActiveBtn(1)}
      className={`${styles.option} ${activeBtn === 1 ? styles.active : null}`}
    >
      <Link to="/login" className={styles.option}>
        Log In
      </Link>
    </li>
  );
  const registerContent = (
    <li
      onClick={() => setActiveBtn(2)}
      className={`${styles.option} ${activeBtn === 2 ? styles.active : null}`}
    >
      <Link to="/register" className={styles.option}>
        Register
      </Link>
    </li>
  );
  const logoutContent = (
    <li onClick={() => setActiveBtn(3)} className={`${styles.option} `}>
      <div className={styles.profilePicContainer}>
        <img src={profile} alt="" className={styles.profilePic} />
        <div className={styles.profileDropdown}>
          <ul>
            <li>
              <Link>Profile</Link>{" "}
            </li>
            <li>
              <Link>Log Out</Link>{" "}
            </li>
          </ul>
        </div>
      </div>

      {/* <Link to="/landing" className={styles.option}>

      </Link> */}
    </li>
  );
  /* -------------------------------------------------------------------------- */

  return (
    <div className={styles.container}>
      {/* -------------------------------- Nav & hamburger ------------------------------- */}
      <SideNav />
      <div className={styles.hamburger} onClick={() => setOpen(!open)}>
        <span className={styles.line}></span>
        <span className={styles.line}></span>
        <span className={styles.line}></span>
      </div>
      {/* ---------------------- Log In and Register OR logout --------------------- */}
      {/* onClick={() => setActiveBtn(1)}
      className={`${styles.option} ${activeBtn === 1 ? styles.active : null}`} */}
      <Link
        to="/"
        className={styles.imageContainer}
        onClick={() => setActiveBtn(0)}
      >
        <img src={logo} alt="" className={styles.image} />
      </Link>

      <ul className={`${styles.optionList} ${open ? styles.open : null}`}>
        {user ? [signinContent, registerContent] : logoutContent}
      </ul>
    </div>
  );
};

export default NavBar;
