import styles from "./ChatBox.module.css";
import React, { useState, useEffect, useRef } from "react";
import { useMutation, useQuery, useLazyQuery } from "@apollo/client";
import useChatRoom from "../useChatRoom";
import useMessages from "../useMessages";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";

const ChatBox = ({ currentFriend, chooseRoomPage, setChooseRoomPage }) => {
  const messageRef = useRef();
  const { messageList, addMessage, setConvoId } = useMessages();
  const { messages, sendMessage } = useChatRoom(messageList);
  const [newMessage, setNewMessage] = useState("");
  const [finalMessageList, setFinalMessageList] = useState(null);
  const room = useAppSelector((state) => state.currentRoom.value);

  const scrollToBottom = () => {
    messageRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(() => {
    if (chooseRoomPage === false) {
      scrollToBottom();
    }
  }, [chooseRoomPage]);
  const renderMessages = () => {
    // ref={messageRef}
    const listLength = messages.length;
    return messages.map((msg, index) => {
      if (index === listLength - 1) {
        return (
          <p
            key={index}
            ref={messageRef}
            className={`${msg.isOwner ? styles.blue : styles.red}
            ${styles.msg}`}
          >
            {msg.body}
          </p>
        );
      }
      return (
        <p
          key={index}
          className={`${msg.isOwner ? styles.blue : styles.red} 
          ${styles.msg}`}
        >
          {msg.body}
        </p>
      );
    });
  };
  const onSubmit = (e) => {
    e.preventDefault();
    if (newMessage !== "") {
      sendMessage(newMessage);
    }
    // addMessage()
    setNewMessage("");
  };
  /* -------------------------------------------------------------------------- */
  return (
    <div className={styles.container}>
      <h2 className={styles.friendNameContainer}>
        <div onClick={() => setChooseRoomPage(!chooseRoomPage)}>go back</div>
        {currentFriend ? <h1>Chatting With: {currentFriend}</h1> : null}
      </h2>
      <div className={styles.messageContainer}>
        {room === "" ? (
          <div className={styles.noRoomMessageContainer}>
            Please Select a <br /> Room To Join
            <div ref={messageRef}>{/* message ref */}</div>
          </div>
        ) : (
          <div>
            <div className={styles.messageList}>
              {/* messages here */}
              {renderMessages()}
              {chooseRoomPage === false ? <div ref={messageRef}></div> : null}
            </div>
          </div>
        )}

        {/* -------------------------------------------------------------------------- */}
      </div>
      <div className={styles.inputBtnContainer}>
        <form action="" onSubmit={onSubmit}>
          <input
            type="text"
            name=""
            id=""
            placeholder="Start Chatting"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
          <button>Send</button>
        </form>
      </div>
    </div>
  );
};

export default ChatBox;
