import styles from "./PostList.module.css";
import { useAppSelector, useAppDispatch } from "../../../redux/hooks";
import React, { useState, useEffect } from "react";
import { useMutation, useQuery } from "@apollo/client";
import mutation from "../../../GQL/mutations/EditUser";
import getCurrentUser from "../../../GQL/queries/CurrentUser";
import Post from "../Post/Post";

const PostList = () => {
  // let { loading, error, data } = useQuery(getCurrentUser);
  const currentUser = useAppSelector((state) => state.currentUser.value);
  const [editUser] = useMutation(mutation);
  const [user, setUser] = useState(null);
  const [text, setText] = useState("");
  const [friendList, setFriendList] = useState("");
  const [postList, setPostList] = useState([]);
  const [postListDone, setPostListDone] = useState([]);
  // const [postListDone, setPostListDone] = useState([]);

  const [posts, setPosts] = useState("");

  useEffect(() => {
    if (currentUser.friends) {
      setFriendList(currentUser.friends);
      // console.log("current User", currentUser.friends);
    }
  }, [currentUser]);

  /* ------------------------- creating list of posts ------------------------- */
  useEffect(() => {
    if (friendList) {
      // const completeListOfMyPostsAndFriendPosts = currentUser.posts;
      const newList = friendList.map((friend, index) => {
        return friend.posts;
      });
      const finishedFriendList = newList.flat();
      // console.log(finishedFriendList);
      const completeListOfMyPostsAndFriendPosts =
        currentUser.posts.concat(finishedFriendList);

      // console.log(completeListOfMyPostsAndFriendPosts);
      setPostList(completeListOfMyPostsAndFriendPosts);

      // console.log(newList2);
      // console.log(currentUser.posts);
      // const hi = newList.concat(currentUser.posts);
      // console.log(newList);
      // console.log("new List", hi);

      // await currentUser.posts.map((post, index) => {
      //   // const nameIncludedList = { name: currentUser.name, ...post };
      //   // console.log(nameIncludedList);
      //   newList.concat(post);
      // });

      // setFriendList(postList.concat(newList));
      // console.log;
      // console.log(friendList, "done1");
      // setPostListDone(true);

      // console.log(currentUser.posts, "user posts");
      // currentUser.posts.map((post, index) => {
      //   const nameIncludedList = { name: currentUser.name, ...post };
      //   setPostList(postList.concat(nameIncludedList));
      //   return null;
      // });
    }
  }, [friendList]);

  /* -------------------------- sorting list of posts ------------------------- */
  useEffect(() => {
    if (postList.length > 0) {
      // console.log(postList, "final post list");
      // postList.sort((a, b) => (a.time > b.time ? -1 : 1));
      // console.log(postList);
      setPostListDone(postList.sort((a, b) => (a.time > b.time ? -1 : 1)));
    }
  }, [postList]);

  // const renderPosts = () => {
  //   return posts.map((post, index) => {
  //     return (
  //       <div key={index} className={styles.individualPost}>
  //         <h3>{user.email}</h3> edit post/delete post here <button>Edit</button>{" "}
  //         time/date here
  //         <h2>{post.post}</h2>
  //         <button>Like</button>
  //         <hr />
  //         comments//// <button>Comment</button>
  //         <br />
  //         post.comments here
  //       </div>
  //     );
  //   });
  // };
  const renderPosts = () => {
    console.log(postListDone, "done");
    return postListDone.map((post, index) => {
      const d = new Date(parseInt(post.time));
      const date = d.toString();
      // console.log(typeof date.toString());

      return (
        <div key={index}>
          <Post
            name={post.userId.name}
            date={date}
            postId={post.id}
            postContent={post.post}
            likeCount={post.likedBy.length}
            commentCount={post.comments.length}
          />
        </div>
      );
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.postListContainer}>{renderPosts()}</div>
    </div>
  );
};

export default PostList;
