              id=""
              placeholder="What's on your mind, Name Here?"
            />
          </div>
        </div>

        <div className={styles.postBtnContainer}>
          <button className={styles.postBtn}>Post</button>
        </div>
      </div>
    </Modal>
  );
};

export default PostForm;
