import styles from "./CommentList.module.css";
import React, { useState, useEffect } from "react";
import Comment from "../Comment/Comment";

const CommentList = ({ commentList, commentListOpen, setCommentListOpen }) => {
  useEffect(() => {
    console.log(commentList, "Comment list");
  });

  const renderCommentsForPost = () => {
    return commentList.map((comment, index) => {
      console.log(comment, "comment");
      return (
        <div id={index}>
          <Comment commentContent={comment.comment} />
        </div>
      );
    });
  };

  return (
    <div
      className={` ${commentListOpen ? styles.animation : styles.container}`}
    >
      {commentListOpen ? renderCommentsForPost() : null}
    </div>
  );
};

export default CommentList;
