import React, { useState, useEffect } from "react";
import styles from "./App.module.css";
import { Router, Route, Switch, useLocation } from "react-router-dom";
import history from "./utils/history";
import NavBar from "./components/NavBar/NavBar";
import LandingPage from "./_pages/LandingPage/LandingPage";
import SideNav from "./components/SideNav/SideNav";
import HomePage from "./_pages/HomePage/HomePage";
import SideBar from "./components/SideBar/SideBar";
import LoginPage from "./_pages/LoginPage/LoginPage";
import RegisterPage from "./_pages/RegisterPage/RegisterPage";

function App(props: any) {
  const [currentRoute, setCurrentRoute] = useState(document.location.pathname);

  useEffect(() => {
    console.log(currentRoute);
  }, [currentRoute]);

  return (
    <div className={styles.App}>
      <Router history={history}>
        {/* <Switch> */}
        <NavBar />
        <div className={styles.SideBar}>
          <SideBar />
        </div>
        {/* ------------------------------- Full Pages ------------------------------- */}
        <div className={styles.fullPages}>
          <Route path="/" exact component={LandingPage} />
          <Route path="/login" exact component={LoginPage} />
          <Route path="/register" exact component={RegisterPage} />
        </div>
        {/* ------------------------------- Dynamic Pages ------------------------------- */}
        <div className={styles.dynamicPages}>
          <Route path="/home" exact component={HomePage} />
        </div>
        {/* </Switch> */}
        {/* {(location = useLocation())} */}
        {console.log(currentRoute)}
      </Router>
    </div>
  );
}

export default App;
